function Home(){var owl=$(".slide-home");owl.owlCarousel({autoPlay:!0,singleItem:!0,pagination:!0,slideSpeed:250,transitionStyle:"fade"})}
function destacados(){var owl=$(".slide-destacados");owl.owlCarousel({autoPlay:!0,items:4,itemsTablet:[600,1],itemsMobile:[400,1],pagination:!1});$(".next").click(function(){owl.trigger('owl.next')})
$(".prev").click(function(){owl.trigger('owl.prev')})}
function modals(e){$(".open-notice").click(function(e){var id_notice=$(this).attr("notice");$('.cont-modal').css({'height':'0','opacity':'0'});$('.container-modals').css({'margin-top':'100%'});if($('.'+id_notice).css('height')=="0px"){$('.'+id_notice).css({'height':'100%','opacity':'1'});setTimeout(function(){$('.container-modals').css({'margin-top':'0'})},500)}
console.log(id_notice)})
$(".close-modals").click(function(e){$('.container-modals').css({'margin-top':'100%'});setTimeout(function(){$('.cont-modal').css({'height':'0','opacity':'0'})},500)})
$(".button-cerrar").click(function(e){$('.container-modals').css({'margin-top':'100%'});setTimeout(function(){$('.cont-modal').css({'height':'0','opacity':'0'})},500)})}
function menu(e){$(".open-menu").click(function(e){$('.links').css('left','0');$('.links').css('opacity','1');$('.cerrar-menu').css('left','0')})
$(".cerrar-menu").click(function(e){$('.links').css('left','-100%');$('.links').css('opacity','0');$('.cerrar-menu').css('left','-100%')})
$(".open-bar1").click(function(e){$('.bars').css('display','none');$('.bar1').css('display','inherit')})
$(".open-bar2").click(function(e){$('.bars').css('display','none');$('.bar2').css('display','inherit')})
$(".cerrar-promo").click(function(e){$('.promo').css('left','-100%');$('.promo').css('opacity','0')})
$(".cerrar-mensaje").click(function(e){$('.title-men').css('display','none')})
var ventana_ancho=$(window).width();$(".btn-publicidad").click(function(){$(".ul-publicidad").toggleClass("active1");console.log('click')});$(".cerrar-toggle").click(function(e){if(ventana_ancho<800){$('.links').css('left','-100%');$('.links').css('opacity','0');$('.cerrar-menu').css('left','-100%')}})}
function Cuadros(){var imagena=parseInt($('.categoria-box').css('width'));$('.categoria-box').css('height',imagena)};$(document).ready(function(){Home();menu();Cuadros();destacados();modals()});$(window).resize(function(){Home()})